        const rawRequestBody = { username: "username", password: "password" };
        const rawResponseBody = { token: "token" };
        server
            .mockEndpoint()
            .post("/auth/token")
            .jsonBody(rawRequestBody)
            .respondWith()
            .statusCode(200)
